// Verarbeite Paperless-NGX API Response
const data = $input.first().json;

if (!data.results || data.results.length === 0) {
  return [];
}

const documents = data.results;
const processedItems = [];

// Hole bereits verarbeitete Dokumente aus dem Workflow Memory
// (In Production solltest du eine echte Datenbank oder Redis verwenden)
const processedDocs = $workflow.getStaticData('global').processedDocs || [];

for (const doc of documents) {
  // PrÃ¼fe ob Dokument bereits verarbeitet wurde
  if (processedDocs.includes(doc.id)) {
    continue;
  }

  // Baue Tag-Array aus den Tag-Objekten
  const tagNames = doc.tags ? doc.tags.map(tag => typeof tag === 'object' ? tag.name : tag) : [];

  // Erstelle formatierte Dokument-Daten
  const processedDoc = {
    document_id: doc.id,
    title: doc.title || 'Unbenanntes Dokument',
    created: doc.created,
    content: doc.content || '',
    tags: tagNames,
    download_url: `https://YOUR_PAPERLESS_URL/api/documents/${doc.id}/download/`,
    web_url: `https://YOUR_PAPERLESS_URL/documents/${doc.id}/`,
    correspondent: doc.correspondent ? doc.correspondent.name : null,
    document_type: doc.document_type ? doc.document_type.name : null,
    archive_serial_number: doc.archive_serial_number || null
  };

  processedItems.push(processedDoc);

  // Markiere Dokument als verarbeitet
  processedDocs.push(doc.id);
}

// Speichere aktualisierte Liste der verarbeiteten Dokumente
$workflow.getStaticData('global').processedDocs = processedDocs;

// Begrenze die Liste auf die letzten 1000 EintrÃ¤ge um Speicher zu sparen
if (processedDocs.length > 1000) {
  $workflow.getStaticData('global').processedDocs = processedDocs.slice(-1000);
}

console.log(`Gefunden: ${documents.length} Dokumente, Neu: ${processedItems.length}`);

return processedItems.map(item => ({ json: item }));

// Log erfolgreich verarbeitete Dokumente
const item = $input.first().json;

console.log(`âœ… Erfolgreich an Apple Shortcuts gesendet:`);
console.log(`   Dokument ID: ${item.document_id}`);
console.log(`   Titel: ${item.title}`);
console.log(`   Tags: ${item.tags ? item.tags.join(', ') : 'Keine'}`);

return [{
  json: {
    success: true,
    document_id: item.document_id,
    title: item.title,
    timestamp: new Date().toISOString()
  }
}];

// Fehlerbehandlung
const error = $input.first().json;

console.error(`âŒ Fehler beim Senden an Apple Shortcuts:`);
console.error(`   Fehler: ${error.message || 'Unbekannter Fehler'}`);
console.error(`   Dokument: ${error.document_id || 'Unbekannt'}`);

// Optional: Sende Fehler-Notification (z.B. an Slack, Discord, etc.)
// Hier kÃ¶nntest du einen weiteren HTTP Request zu einem Monitoring-System machen

return [{
  json: {
    error: true,
    message: error.message || 'Webhook failed',
    document_id: error.document_id || null,
    timestamp: new Date().toISOString()
  }
}];

// Keine neuen Dokumente gefunden
console.log('â„¹ï¸ Keine neuen Todo-Dokumente gefunden');

return [{
  json: {
    message: 'No new documents found',
    timestamp: new Date().toISOString(),
    total_processed: $workflow.getStaticData('global').processedDocs?.length || 0
  }
}];

// Memory Cleanup - Bereinige alte EintrÃ¤ge periodisch
const processedDocs = $workflow.getStaticData('global').processedDocs || [];
const now = new Date();
const oneWeekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);

// In einer echten Implementierung wÃ¼rdest du hier Timestamps verwenden
// FÃ¼r jetzt behalten wir nur die letzten 500 Dokument-IDs
if (processedDocs.length > 500) {
  $workflow.getStaticData('global').processedDocs = processedDocs.slice(-500);
  console.log(`ðŸ§¹ Memory cleanup: Reduced processed docs from ${processedDocs.length} to 500`);
}

return [{ json: { cleanup: true, timestamp: now.toISOString() } }];
